function ValidateEmail(inEmail){
    var mailformat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    try {
    if(inEmail.match(mailformat))
        {
            return true;
        }
    else
        {
            frappe.msgprint(__('Check your Email!'));
        }
    } catch (err) {
        frappe.msgprint(__('Check your Email!'));
    }
}

function ValidatePhoneNumber(inNumber){
    var countryCodeFormat = /^\+?\d+$/;
    try{
    if(inNumber.match(countryCodeFormat)[0])
        {
            return true;
        }
    else
        {
            frappe.msgprint(__('Check your Phone Number!'));
        }
    } catch (err){
        frappe.msgprint(__('Check your Phone Number!'));
    }
}

frappe.web_form.validate = () => {
    if (ValidateEmail(frappe.web_form.get_value('email_address')) &&
        ValidatePhoneNumber(frappe.web_form.get_value('phone_number'))){
        return true ;
    } else {
        return ;
    }
}

$.getJSON('https://api.db-ip.com/v2/free/self', function(data) {
  var userSessionData = data;

  frappe.web_form.set_value('ipaddress', userSessionData.ipAddress);
  frappe.web_form.set_value('continentname', userSessionData.continentName);
  frappe.web_form.set_value('continentcode', userSessionData.continentCode);
  frappe.web_form.set_value('countryname', userSessionData.countryName);
  frappe.web_form.set_value('countrycode', userSessionData.countryCode);
  frappe.web_form.set_value('stateprov', userSessionData.stateProv);
  frappe.web_form.set_value('city', userSessionData.city);

});

frappe.web_form.after_save= () => {
   window.location = 'https://ao-erpnext.sky.slnee.com/culturequiz-data/' + frappe.web_form.get_value('full_name').trim().replaceAll(' ', '-') + '?_lang=' + lang;
}

function hideWebformHeader(){
    var wh = document.getElementsByClassName('page-header')[0];
    wh.style.display = 'none';

    var wa = document.getElementsByClassName('page-header-actions-block')[0];
    wa.style.display = 'none';

    document.getElementsByTagName("hr")[0].style.display = 'none';
}
