client_script:
var urlParams = new URLSearchParams(window.location.search);

var schoolName = urlParams.get('school_name');

frappe.web_form.set_value('school_name', schoolName);

function ValidateEmail(inEmail){
    var mailformat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    try {
    if(inEmail.match(mailformat))
        {
            return true;
        }
    else
        {
            frappe.msgprint(__('Check your Email!'));
        }
    } catch (err) {
        frappe.msgprint(__('Check your Email!'));
    }
}

function ValidatePhoneNumber(inNumber){
    var countryCodeFormat = /^\+?\d+$/;
    try{
    if(inNumber.match(countryCodeFormat)[0])
        {
            return true;
        }
    else
        {
            frappe.msgprint(__('Check your Phone Number!'));
        }
    } catch (err){
        frappe.msgprint(__('Check your Phone Number!'));
    }
}

frappe.web_form.validate = () => {
    if (ValidateEmail(frappe.web_form.get_value('email_address')) &&
        ValidatePhoneNumber(frappe.web_form.get_value('phone_number'))){
        return true ;
    } else {
        return false ;
    }
}

// adding new teacher with the same scholl name..
var addNewTeacherBtnFlag = false;
const addNewTeacher = function (){
    addNewTeacherBtnFlag = true;
    frappe.web_form.save();
}
// custom button for adding new teacher..
addNewTeacherBtn = document.createElement('button');
addNewTeacherBtn.id = 'addNewTeacherBtnID';
addNewTeacherBtn.className = 'btn btn-info btn-sm ml-2';
addNewTeacherBtn.innerHTML = 'Add New Teacher';
addNewTeacherBtn.style.margin = '2px';
document.getElementsByClassName('web-form-footer pull-right')[0].appendChild(addNewTeacherBtn);
addNewTeacherBtn.addEventListener('click', addNewTeacher);


