frappe.web_form.after_save = ()=> {
    if (addNewTeacherBtnFlag) {
        addNewTeacherBtnFlag = false;
        window.location = `https://cdafricaa.com/school-teacher-registration?new=1&school_name=${schoolName}`;
    } else {
        window.location = frappe.web_form.success_url;
    }
}
